def solution(S, C):
    # write your code in Python 3.8.10
    # 영어 소문자로 구성된 N 짜리 문자열 S
    # C의 내용대로 M에다가 '$' 넣을거임

    # 그니까 연속되는 문자가 없도록 만들건데.
    # C의 적혀진 순서대로 $를 넣을거야.
    # 그래서 연속되는 문자가 없을떄까지 진행해서, 없어질떄 $ 사용한 갯수를 구해라.

    # 계속 만족하는지 확인하면 시간이 오래걸리기 때문에
    # 필요한 위치들을 미리 저장해놓고 삭제하는 방식으로 진행.

    # 먼저 문자삽입 필요한 위치부터 찾자.

    C = [i for i in range(50000)]

    def isEnd():

        for values in alphawant.values():
            if len(values) != 0:
                return False
        return True

    alphaCheck = {}
    alphawant = {}
    for idx in range(len(S)):
        if S[idx] in alphaCheck:
            if S[idx] in alphawant:
                alphawant[S[idx]].append(
                    {i+1 for i in range(alphaCheck[S[idx]], idx)})
            else:
                alphawant[S[idx]] = [
                    {i+1 for i in range(alphaCheck[S[idx]], idx)}]
            alphaCheck[S[idx]] = idx
        else:
            alphaCheck[S[idx]] = idx

    state = isEnd()
    if state:
        return 0
    answer = 0

    for idx in C:
        isDelete = False
        delList = []
        for key in alphawant.keys():
            temp = []
            while alphawant[key]:
                t = alphawant[key].pop()
                if idx in t:
                    isDelete = True
                    break
                else:
                    temp.append(t)
            alphawant[key] = alphawant[key] + temp

        if isDelete:
            state = isEnd()

        if state:
            return answer+1
        else:
            answer += 1

    return -1


print(solution(
    '', []))
